import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import config from '../../config/config';

const CreateProject = () => {
  const navigate = useNavigate();
  const [project, setProject] = useState({
    title: '',
    description: '',
    imageUrl: '',
    technologies: '',
    githubUrl: '',
    liveUrl: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProject(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${config.API_URL}/projects`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...project,
          technologies: project.technologies.split(',').map(tech => tech.trim())
        })
      });

      if (response.ok) {
        navigate('/admin/projects');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="admin-form-container">
      <h2>Crear Nuevo Proyecto</h2>
      <form onSubmit={handleSubmit} className="admin-form">
        <div className="form-group">
          <label htmlFor="title">Título</label>
          <input
            type="text"
            id="title"
            name="title"
            value={project.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Descripción</label>
          <textarea
            id="description"
            name="description"
            value={project.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="imageUrl">URL de la Imagen</label>
          <input
            type="text"
            id="imageUrl"
            name="imageUrl"
            value={project.imageUrl}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="technologies">Tecnologías (separadas por comas)</label>
          <input
            type="text"
            id="technologies"
            name="technologies"
            value={project.technologies}
            onChange={handleChange}
            placeholder="React, Node.js, MongoDB"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="githubUrl">URL de GitHub</label>
          <input
            type="url"
            id="githubUrl"
            name="githubUrl"
            value={project.githubUrl}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="liveUrl">URL del Proyecto</label>
          <input
            type="url"
            id="liveUrl"
            name="liveUrl"
            value={project.liveUrl}
            onChange={handleChange}
          />
        </div>

        <div className="form-buttons">
          <button type="submit" className="btn-primary">Crear Proyecto</button>
          <button 
            type="button" 
            className="btn-secondary"
            onClick={() => navigate('/admin/projects')}
          >
            Cancelar
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateProject;
